mapper.select:
SELECT d.id, d.name, d.category_id, d.price, d.description, d.image, d.status, d.create_time, d.create_user, d.update_time, d.update_user, c.name AS category_name FROM dish d LEFT JOIN category c ON d.category_id = c.id
AND d.name LIKE CONCAT('%', #{name}, '%')
AND d.category_id = #{categoryId}
AND d.status = #{status}
ORDER BY d.update_time DESC

mapper.delete:
DELETE FROM dish
WHERE id IN

    #{id}
